function pw() {
        var pw =(Number($("#pp").clientWidth) - Number($("#pps").clientWidth));
          if (pw <= 0) {
          $("#ps").classList.add("dispw")
        }
        if (pw > 0 && $("#ps").classList.contains("dispw")) {
         $("#ps").classList.remove("dispw");
        }
        return pw;
      }


